public class while2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		   int d = 50;
		    
		   while (d>=20) {
			   System.out.print(d +" ");
			   d--;
		   }
		   
		   int b=2;
		   while (b<=20) {
				   System.out.println(b+" ");
				   b+=2;
			   }
		   
		   int i=1;
		   while (i<=20) {
			   if (i%2 ==0) {
				   System.out.print(i+" ");
			   }i++;
		   }
		   }
		   
		   
	}


